import { createI18n } from "vue-i18n";
import textsEN from "./i18nTexts/en/texts.js";
import textsPt from "./i18nTexts/pt-br/texts.js";

const messages = {
  en: {
    paragraphOne: textsEN.paragraphOne,
    paragraphTwo: textsPt.paragraphOne,
    paragraphThree: textsPt.paragraphOne,
    paragraphFour: textsPt.paragraphOne,
  },
  pt: {
    paragraphOne: textsPt.paragraphOne,
    paragraphTwo: textsPt.paragraphOne,
    paragraphThree: textsPt.paragraphOne,
    paragraphFour: textsPt.paragraphOne,
  },
};

const i18n = createI18n({
  locale: "en",
  messages,
});

export default i18n;
