import { createI18n } from "vue-i18n";

const messages = {
  en: {
    welcome: "Welcome",
    goodbye: "Goodbye",
  },
  pt: {
    welcome: "Bienvenido",
    goodbye: "Adiós",
  },
};

const i18n = createI18n({
  locale: "en",
  messages,
});

export default i18n;
